function labels = extendCluster(X, labels, p, hood, C, eps, minPts)    % extend a cluster with label C from the seed p    % you can define this function recursively. or use a while loop with a queue.        labels(p,1) = C;    i = 1;    while i < size(hood)(1,1)      %hood       Pnext = i;       if labels(hood(i,3),1) == -1         labels(hood(i,3),1) = C;       else if labels(hood(i,3),1) == 0         labels(hood(i,3),1) = C;              end       hood_prime = findNeighbors(X, hood(i,3), eps);       %hood_prime       if size(hood_prime)(1,1) > minPts         hood = [hood; hood_prime];         [hood,ia,ic] = unique(hood(:,1:3),'rows');                         end       i++;       %i       %length(hood)    end     end